import './index.scss'

const Count = ({ onPlus, onMinus, count, setVisible,cartList }) => {
  const minusAndExit = () => {
    onMinus(); // 执行减少数量
    // 检查购物车是否为空
    if (cartList.length === 0) { 
      setVisible(false); // 关闭购物车
    }
  };
  return (
    <div className={"goods-count"}>
      <span className="minus" onClick={minusAndExit}>-</span>
      <span className="count">{count}</span>
      <span className="plus" onClick={onPlus}>+</span>
    </div>
  )
}

export default Count
